const tips=["Use a combination of letters, numbers, and special characters for a strong password.","Avoid using the same password for multiple accounts.","Consider using a password manager to store and manage your passwords securely.","Change your passwords regularly, especially for sensitive accounts.","Never share your passwords with anyone.",];function displayRandomTip(){const randomIndex=Math.floor(Math.random()*tips.length);const randomTipElement=document.getElementById("randomTip");randomTipElement.classList.remove("pop-in");void randomTipElement.offsetWidth;randomTipElement.classList.add("pop-in");randomTipElement.innerText=`Tip: ${tips[randomIndex]}`;}
setInterval(displayRandomTip,3000);;