document.getElementById("resetPasswordForm").addEventListener("submit",function(event){event.preventDefault();const newPassword=document.querySelector('input[name="new_password"]').value;const confirmPassword=document.querySelector('input[name="new_password_confirm"]').value;const submitButton=document.querySelector(".btn");const csrfToken=document.querySelector("[name=csrfmiddlewaretoken]").value;if(newPassword!==confirmPassword){showResetPasswordError("Passwords do not match.");return;}
const passwordStrengthRegex=/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;if(!passwordStrengthRegex.test(newPassword)){showResetPasswordError("Password must be at least 8 characters long, contain both uppercase and lowercase letters, include at least one numeric digit, and one special character.");return;}
showResetPassLoader();submitButton.disabled=true;fetch(window.location.href,{method:"POST",headers:{"Content-Type":"application/x-www-form-urlencoded","X-CSRFToken":csrfToken,},body:new URLSearchParams({new_password:newPassword,new_password_confirm:confirmPassword,}),}).then((response)=>{if(response.ok){smoothTransitionToCompletePage();}else{return response.text().then((text)=>{hideResetPassLoader();showResetPasswordError("An error occurred. Please try again.");submitButton.disabled=false;});}}).catch((error)=>{hideResetPassLoader();console.error("Error:",error);showResetPasswordError("An error occurred. Please try again later.");submitButton.disabled=false;});});function showResetPassLoader(){document.getElementById("resetpassOverlay").style.display="block";}
function hideResetPassLoader(){document.getElementById("resetpassOverlay").style.display="none";}
function showResetPasswordError(message){const errorBox=document.getElementById("resetPasswordErrorBox");document.getElementById("resetPasswordErrorContent").innerText=message;errorBox.classList.remove("pop-out");errorBox.classList.add("pop-in");errorBox.style.display="block";setTimeout(()=>{errorBox.classList.remove("pop-in");errorBox.classList.add("pop-out");setTimeout(()=>{errorBox.style.display="none";errorBox.classList.remove("pop-in","pop-out");},300);},3000);}
function smoothTransitionToCompletePage(){const resetContainer=document.getElementById("reset-container");resetContainer.classList.add("fade-out");setTimeout(()=>{window.location.href="/reset/done/";},600);}
document.querySelectorAll(".curved-line path").forEach(function(path){var controlPointX1=Math.random()*50;var controlPointY1=Math.random()*50;var controlPointX2=100-Math.random()*50;var controlPointY2=Math.random()*50;var endPointX=Math.random()*100;var endPointY=Math.random()*100;var d=`M0,0 C${controlPointX1},${controlPointY1} ${controlPointX2},${controlPointY2} ${endPointX},${endPointY}`;path.setAttribute("d",d);});;const tips=["Use a combination of letters, numbers, and special characters for a strong password.","Avoid using the same password for multiple accounts.","Consider using a password manager to store and manage your passwords securely.","Change your passwords regularly, especially for sensitive accounts.","Never share your passwords with anyone.",];function displayRandomTip(){const randomIndex=Math.floor(Math.random()*tips.length);const randomTipElement=document.getElementById("randomTip");randomTipElement.classList.remove("pop-in");void randomTipElement.offsetWidth;randomTipElement.classList.add("pop-in");randomTipElement.innerText=`Tip: ${tips[randomIndex]}`;}
setInterval(displayRandomTip,3000);;